import React from 'react';
import defaultImg from '../Utility/defaultImg.jpg';
const PokemonThumb = ({ key, pokemon }) => {
    const showBack = () => {
        document.getElementById(key).src = pokemon.sprites.back_default;
    }

    return (
        <div>
            <img src={pokemon.sprites.front_default} alt={defaultImg} id={key} onFocus="showBack()" />
            {key}
        </div>
    )
}

export default PokemonThumb;
