import React from "react";
// import defaultImg from '../Utility/defaultImg.jpg';
//components

import Loading from "../components/Spinner";
import ShowMore from "../components/ShowMore";
import Grid from "../components/Grid";
import PokemonThumb from '../components/PokemonThumb';
//hooks
import { useHomeFetch } from "../hooks/getPokemonPage";


const Home = () => {
    const { state, loading, error, setIsLoadingMore, isLoadingMore } = useHomeFetch();
    console.log(state);
    if (error) {
        return (
            <div>
                AN ERROR OCCURED :(
            </div>
        )
    }
    return (
        <>
            <Grid>
                {
                    state.pokemons.map(element => (
                        <PokemonThumb image={element.sprites.front_default} key={element.id} />
                    ))
                }
            </Grid>

            {loading && <Loading />}
            {!isLoadingMore && state.pokemons.length < 802 && <ShowMore callback={() => setIsLoadingMore(true)} />}
        </>
    )
}

export default Home;
